import React from 'react';
import styles from './burger-project.module.css';
import PropTypes from 'prop-types';
import BurgerElement from '../burger-element/burger-element';
import { ConstructorElement, CurrencyIcon, DragIcon, Button } from '@ya.praktikum/react-developer-burger-ui-components';

import ingredientPropType from '../../../utils/prop-types';

function BurgerProject({ dataArr }) {
    const outerBun = dataArr.find(el => el.type === "bun");

    const ingredientsArray = dataArr.filter((el) => {
        return el.type !== "bun"
    })

    return (
        <>
            <div className={styles.container}>
                <ConstructorElement
                    type="top"
                    isLocked={true}
                    text={`${outerBun.name} (верх)`}
                    price={outerBun.price}
                    thumbnail={outerBun.image}
                />
            </div>

            {ingredientsArray.map((el) => {
                return <div key={el._id} className={styles.container}>
                    <DragIcon type="primary" />
                    <ConstructorElement
                        text={el.name}
                        price={el.price}
                        thumbnail={el.image}
                    />

                </div>
            })}


            <div className={styles.container}>
                <ConstructorElement
                    type="bottom"
                    isLocked={true}
                    text={`${outerBun.name} (низ)`}
                    price={outerBun.price}
                    thumbnail={outerBun.image}
                />
            </div>
            </>
    )
}
BurgerProject.propTypes = {
    dataArr: PropTypes.array.isRequired
};
            export default BurgerProject;
