import React from 'react';
import styles from './burger-project.module.css';
import PropTypes from 'prop-types';
import BurgerElement from '../burger-element/burger-element';
import { ConstructorElement, CurrencyIcon, DragIcon, Button } from '@ya.praktikum/react-developer-burger-ui-components';

import ingredientPropType from '../../../utils/prop-types';

function BurgerProject({ dataArr }) {
    const result = dataArr.reduce((acc, el) => {
        if (el.type === 'bun') {
            acc.Bun = el;
        } else {
            acc.ElmArr.push(el);
        }
        return acc;
    }, { Bun: null, ElmArr: [] });

    const { Bun, ingredientsArray } = result;

    return (
        <>
            <div className={styles.container}>
                <ConstructorElement
                    type="top"
                    isLocked={true}
                    text={`${Bun.name} (верх)`}
                    price={Bun.price}
                    thumbnail={Bun.image}
                />
            </div>

            {
                ingredientsArray.map(({ _id, name, price, image }) => (
                    <div key={_id} className={styles.container}>
                        <DragIcon type="primary" />
                        <ConstructorElement text={name} price={price} thumbnail={image} />
                    </div>
                ))
            }



            <div className={styles.container}>
                <ConstructorElement
                    type="bottom"
                    isLocked={true}
                    text={`${Bun.name} (низ)`}
                    price={Bun.price}
                    thumbnail={Bun.image}
                />
            </div>
        </>
    )
}
BurgerProject.propTypes = {
    dataArr: PropTypes.array.isRequired
};
export default BurgerProject;
