import React, { useState, useRef } from 'react';
import { Tab, CurrencyIcon, Counter } from '@ya.praktikum/react-developer-burger-ui-components';
import styles from './burger-ingredients.module.css';
import { ingredientPropType } from '../../utils/prop-types';
import PropTypes from 'prop-types';

const BurgerIngredients = ({ data, addIngredientToCart }) => {
    const [current, setCurrent] = useState('Булки');

    const categoryLinks = {
        'Булки': useRef(),
        'Соусы': useRef(),
        'Начинки': useRef(),
    };

    const handleTabClick = (tab) => {
        setCurrent(tab);
        categoryLinks[tab].current.scrollIntoView({ behavior: 'smooth' });
    };

    function filterByType(data, type) {
        return data.filter(item => item.type === type);
    }

    const categoryItems = {
        'Булки': filterByType(data, 'bun'),
        'Соусы': filterByType(data, 'sauce'),
        'Начинки': filterByType(data, 'main'),
    };

    return (
        <section className={styles.section}>
            <h1 className='text text_type_main-large'>Соберите бургер</h1>
        </section>
    );
};

BurgerIngredients.propTypes = {
    data: PropTypes.arrayOf(ingredientPropType).isRequired,
    addIngredientToCart: PropTypes.func.isRequired,
};

export default BurgerIngredients;
