import React from 'react';
import styles from './burger-project.module.css';
import PropTypes from 'prop-types';
import BurgerElement from '../burger-element/burger-element';
import { ConstructorElement, CurrencyIcon, DragIcon, Button } from '@ya.praktikum/react-developer-burger-ui-components';

import ingredientPropType from '../../../utils/prop-types';



function BurgerProject({ burgerArr }) {
    const { Bun, ElmArr } = burgerArr.reduce(
        (acc, { type, ...props }) => {
            if (type === 'bun') {
                acc.Bun = props;
            } else {
                acc.ElmArr.push(props);
            }
            return acc;
        },
        { Bun: null, ElmArr: [] }
    );

    return (
        <>
            <div className={styles.container}>
                <ConstructorElement
                    type="top"
                    isLocked={true}
                    text={`${Bun.name} (верх)`}
                    price={Bun.price}
                    thumbnail={Bun.image}
                />
            </div>

            {ElmArr.map(({ _id, name, price, image }) => (
                <div key={_id} className={styles.container}>
                    <DragIcon type="primary" />
                    <ConstructorElement text={name} price={price} thumbnail={image} />
                </div>
            ))}

            <div className={styles.container}>
                <ConstructorElement
                    type="bottom"
                    isLocked={true}
                    text={`${Bun.name} (низ)`}
                    price={Bun.price}
                    thumbnail={Bun.image}
                />
            </div>
        </>
    );
}

BurgerProject.propTypes = {
    burgerArr: PropTypes.arrayOf(IngredientPropType).isRequired,
};

export default BurgerProject;
