import React from 'react'
import {CurrencyIcon, Tab} from '@ya.praktikum/react-developer-burger-ui-components';
import styles from './ingredient-cards.module.css'
import { ingredientPropType } from '../../../utils/prop-types';
import PropTypes from 'prop-types';

function IngredientCards({ header, cardsArr }) {
  return (
    <>
      <h2 className={burgerIngredients.title}>{header}</h2>
      <div className={burgerIngredients.container}>
        {cardsArr.map(({ _id, name, price, image }) => (
          <Ingredient
            name={name}
            price={price}
            image={image}
            key={_id}
          />
        ))}
      </div>
    </>
  );
}

IngredientsContainer.propTypes = {
  header: PropTypes.string.isRequired,
  cardsArr: PropTypes.arrayOf(ingredientPropType).isRequired,
};

export default IngredientsContainer;
