import React from 'react'
import {ConstructorElement, DragIcon} from '@ya.praktikum/react-developer-burger-ui-components'
import styles from './burger-element.module.css'

export default function BurgerElement(dataArr) {

    
  return (
    <ConstructorElement/>
  )
}
const Bun = ({ el, position }) => (
    <div className={`${styles.container}`}>
      <ConstructorElement
        type={position}
        isLocked={true}
        text={`${el.name} (${position === "top" ? "верх" : "низ"})`}
        price={el.price}
        thumbnail={el.image}
      />
    </div>
  );
  
  const Ingredient = ({ el }) => (
    <div key={el._id} className={styles.container}>
      <DragIcon type="primary" />
      <ConstructorElement text={el.name} price={el.price} thumbnail={el.image} />
    </div>
  );
  
  function ConstructorIngredient({ dataArr }) {
    const outerBun = dataArr.find((el) => el.type === "bun");
  
    const ingredientsArray = dataArr.filter((el) => el.type !== "bun");
  
    return (
      <>
        <Bun el={outerBun} position="top" />
  
        {ingredientsArray.map((el) => (
          <Ingredient key={el._id} el={el} />
        ))}
  
        <Bun el={outerBun} position="bottom" />
      </>
    );
  }
