import React from 'react'
import { Tab } from '@ya.praktikum/react-developer-burger-ui-components'
import style from './burger-ingredients.module.css'
import PropsType from '../../utils/prop-types'

class BurgerIngredients extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            current: 'Булки'
        };
        this.categoryRefs = {
            'Булки': React.createRef(),
            'Соусы': React.createRef(),
            'Начинки': React.createRef(),
        };
    }

    handleTabClick = (tab) => {
        this.setState({ current: tab });
        this.categoryRefs[tab].current.scrollIntoView({ behavior: 'smooth' });
    };

    render() {
        const { data, addIngredientToCart } = this.props;
        const { current } = this.state;

        const categorizedItems = {
            'Булки': data.filter(item => item.type === 'bun'),
            'Соусы': data.filter(item => item.type === 'sauce'),
            'Начинки': data.filter(item => item.type === 'main'),
        };

        return (
            <section className={styles.section}>
                <h1 className='text text_type_main-large'>Соберите бургер</h1>
            </section>
        );
    }
}

BurgerIngredients.propTypes = {
    data: PropsType.arrayOf(ingredientPropType).isRequired,
    addIngredientToCart: PropTypes.func.isRequired,
};

export default BurgerIngredients;
