import React from 'react'
import {ConstructorElement, DragIcon} from '@ya.praktikum/react-developer-burger-ui-components'
import styles from './burger-element.module.css'

const BurgerElement= React.memo(({ element, position }) =>{
  
    const isBun = position === "top" || position === "bottom";
    const lock = isBun;
    const type = isBun ? position : null;
    
    return (
        <div className={`${styles.container}`}>
          {!isBun && <DragIcon type="primary" />}
          <ConstructorElement
            type={type}
            isLocked={lock}
            text={`${element.name}${isBun ? ` (${position === "top" ? "верх" : "низ"})` : ""}`}
            price={element.price}
            thumbnail={element.image}
          />
        </div>
    );
});

    
  
function ConstructorIngredient({ dataArr }) {
  const outerBun = dataArr.find((el) => el.type === "bun");

  const ingredientsArray = dataArr.filter((el) => el.type !== "bun");

  return (
    <>
      <BurgerElement element={outerBun} position="top" />

      {ingredientsArray.map((el) => (
        <BurgerElement key={el._id} element={el} />
      ))}

      <BurgerElement element={outerBun} position="bottom" />
    </>
  );
}

export default ConstructorIngredient;
