import React from 'react';
import { ConstructorElement, CurrencyIcon, DragIcon, Button } from '@ya.praktikum/react-developer-burger-ui-components';
import styles from "./burger-constructor.module.css";
import PropTypes from 'prop-types';
import ingredientPropType from '../../utils/prop-types';
import BurgerProject from './burger-project/BurgerProject';
import { data } from '../../utils/data';

function BurgerConstructor() {

  const totalPrice = ingredients.reduce((acc, ingredient) => acc + ingredient.price, 0) + (bun ? bun.price * 2 : 0);

  return (
    <section className={`${styles.list} custom-scroll mt-25`}>
      <BurgerProject burgerArr={data} />
      
      <div className={`${styles.checkout} mr-4 mt-10`}>
                <div className={styles.total}>
                    <span className="text text_type_digits-medium">{totalPrice}</span>
                    <CurrencyIcon type="primary"/>
                </div>
                <Button htmlType="button" type="primary" size="large">
                    Оформить заказ
                </Button>
            </div>
        </section>
   
  )
}




export default BurgerConstructor;
