import React, { Component } from 'react';
import { Tab, CurrencyIcon, Counter} from '@ya.praktikum/react-developer-burger-ui-components';
import style from './burger-ingredients.module.css';
import {ingredientPropType} from '../../utils/prop-types';
import PropTypes from "prop-types";

class BurgerIngredients extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            current: 'Булки'
        };
        this.categoryLinks = {
            'Булки': React.createRef(),
            'Соусы': React.createRef(),
            'Начинки': React.createRef(),
        };
    }

    handleTabClick = (tab) => {
        this.setState({ current: tab });
        this.categoryLinks[tab].current.scrollIntoView({ behavior: 'smooth' });
    };

    render() {
        const { data, addIngredientToCart } = this.props;
        const { current } = this.state;

        function filterByType(data, type) {
            return data.filter(item => item.type === type);
        }

        const categoryItems = {
            'Булки': filterByType(data, 'bun'),
            'Соусы': filterByType(data, 'sauce'),
            'Начинки': filterByType(data, 'main'),
        };

        return (
            <section className={style.section}>
                <h1 className='text text_type_main-large'>Соберите бургер</h1>
            </section>
        );
    }
}

BurgerIngredients.propTypes = {
    data: PropTypes.arrayOf(ingredientPropType).isRequired,
    addIngredientToCart: PropTypes.func.isRequired,
};

export default BurgerIngredients;
